import React, { useState } from 'react';
import axios from 'axios';
import '../'
const SignUp = () => {
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        password: '',
        confirmPassword: '',
    });

    const [isRegistrationSuccess, setRegistrationSuccess] = useState(false);

    const handleChange = (event) => {
        const { name, value } = event.target;
        setFormData((prevFormData) => ({
            ...prevFormData,
            [name]: value,
        }));
    };

    const handleSubmit = (event) => {
        event.preventDefault();

        const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        const passwordPattern = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;

        if (!emailPattern.test(formData.email)) {
            alert('Email không hợp lệ');
            return;
        }

        if (!passwordPattern.test(formData.password)) {
            alert('Invalid password. Password must be at least 8 characters including uppercase, lowercase, numbers and special characters.');
            return;
        }
        if (formData.password !== formData.confirmPassword) {
            alert('Confirmation password does not match. Please check again.');
            return;
        }
        axios.post('https://63aa9f0e7d7edb3ae62c5cea.mockapi.io/user', formData)
            .then((response) => {
                alert('Sign up success!');
                window.location.href = "/login";
                setRegistrationSuccess(true);
            })
            .catch((error) => {
                alert('Sign up fail!', error);
            });
    };

    return (
        
            <form onSubmit={handleSubmit} className="bg">
                {isRegistrationSuccess && (
                    <p className="text">Sign up success!</p>
                )}

                <div className="main2" >
                    <div className="content">
                        <div className="maintext">
                            <div className="text">Register for a free account</div>
                            <div className="py-4 px-8">
                                <div className="mb-4">
                                    <label className="a username" for="first_name">Username</label>
                                    <input className="input"
                                        type="text"
                                        id="username"
                                        name="username"
                                        value={formData.username}
                                        onChange={handleChange}
                                        required
                                        placeholder="Your first name" />
                                </div>
                                <div className="mb-4">
                                    <label className="a email" for="email">Email Address</label>
                                    <input className="input"
                                        type="email"
                                        id="email"
                                        name="email"
                                        value={formData.email}
                                        onChange={handleChange}
                                        required
                                        placeholder="Your email address" />
                                </div>
                                <div className="mb-4">
                                    <label className="a password" for="password">Password</label>
                                    <input className="input"
                                        type="password"
                                        id="password"
                                        name="password"
                                        value={formData.password}
                                        onChange={handleChange}
                                        required
                                        placeholder="Your secure password" />
                                </div>
                                <div className="mb-4">
                                    <label className="a confirmPassword" for="password">Confirm Password </label>
                                    <input className="input"
                                        type="password"
                                        id="confirmPassword"
                                        name="confirmPassword"
                                        value={formData.confirmPassword}
                                        onChange={handleChange}
                                        required />
                                </div>
                                <div className="flex items-center justify-center mt-8">
                                    <button className="btn" style={{ borderRadius: 4 }}>
                                        Sign Up
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </form>
       
    );
};

export default SignUp;
